<script>
    function loadMealSearchPage_URL(url) {

        var csrfToken = $('meta[name="csrf-token"]').attr('content');
        var query = $('#meal_name_1').val();
        var servingSize = $('#meal_servingsize_1').val();
        var quantity = $('#meal_quantity_1').val();
        var foods_pages = $('#foods_pages').val().split(",");

        // console.log("FOODS PAGES SWPF", foods_pages)

        var existsAsItem = jQuery.inArray(query, foods_pages) !== -1 ? true : false;

        // console.log("EXISTS AS ITEM", existsAsItem);

        if (servingSize == "") {
            servingSize = 1;
        }

        if (quantity == "") {
            quantity = 1;
        }

        var ignoreServingSize = $("#disable_servingsize_1").is(':checked');


        $.ajax({
            url: `${url}`,
            method: 'GET',
            headers: {
                'X-CSRF-TOKEN': csrfToken
            },
            data: {
                query: query,
                servingSize: servingSize,
                quantity: quantity,
                existsAsItem: existsAsItem
                //ignoreServingSize: ignoreServingSize
            },
            success: function(response) {
                $('#FOOD-SEARCH-CONTAINER').empty().append(response);
                // console.log('success');
                window.scrollTo(0, 0)
            },
            error: function(xhr) {
                console.log(xhr.responseText);
            }
        });
    }

    function loadMealSearchPage(page) {

        var csrfToken = $('meta[name="csrf-token"]').attr('content');
        var query = $('#meal_name_1').val();
        var servingSize = $('#meal_servingsize_1').val();
        var quantity = $('#meal_quantity_1').val();
        var foods_pages = $('#foods_pages').val().split(",");

        // console.log("FOODS PAGES SWPF", foods_pages)

        var existsAsItem = jQuery.inArray(query, foods_pages) !== -1 ? true : false;

        // console.log("EXISTS AS ITEM", existsAsItem);

        if (servingSize == "") {
            servingSize = 1;
        }

        if (quantity == "") {
            quantity = 1;
        }

        var ignoreServingSize = $("#disable_servingsize_1").is(':checked');

        $.ajax({
            url: `/nutrition/meal/search_food/${query}?page=${page}`,
            method: 'GET',
            headers: {
                'X-CSRF-TOKEN': csrfToken
            },
            data: {
                query: query,
                servingSize: servingSize,
                quantity: quantity,
                existsAsItem: existsAsItem
                //ignoreServingSize: ignoreServingSize
            },
            success: function(response) {
                $('#FOOD-SEARCH-CONTAINER').empty().append(response);
                // console.log('success');
                window.scrollTo(0, 0)
            },
            error: function(xhr) {
                console.log(xhr.responseText);
            }
        });
    }
</script>

